sys_script_client.script:
function onLoad() {

	var val = g_form.getValue('subcategory');
   if (val== 'Risk Issue Action') {
        g_form.addOption('status', 'Open', 'Open');
        g_form.addOption('status', 'Action Completed', 'Action Completed');
        g_form.addOption('status', 'Cancelled', 'Cancelled');
        g_form.addOption('status', 'In-Progress', 'In-Progress');
        g_form.removeOption('status', 'Closed');
        g_form.removeOption('status', 'Expired');
    } else {
        g_form.addOption('status', 'Open', 'Open');
        g_form.addOption('status', 'Closed', 'Closed');
        g_form.addOption('status', 'Expired', 'Expired');
        g_form.addOption('status', 'Cancelled', 'Cancelled');
        g_form.removeOption('status', 'Action Completed');
        g_form.removeOption('status', 'In-Progress');


    }

}
