sys_script_include.script:
var riskAccessRoles = Class.create();
riskAccessRoles.prototype = {
        initialize: function() {},

        getGroup: function(gs,current) {

			var user = gs.getUser();
			var group = current.getDisplayValue('owner_organization');

                    if (((user.isMemberOf(group)) && current.active == true) || user.hasRole(gs.getProperty('x_nuta2_risk_issue.risk.access.roles')))
                    return true;
                },
	getActionGroup: function(gs,current) {

			var user = gs.getUser();
			var group = current.getDisplayValue('action_owner_organization');
                    if (((user.isMemberOf(group)) && current.active == true) || user.hasRole(gs.getProperty('x_nuta2_risk_issue.risk.access.roles')))
                    return true;
                },


                type: 'riskAccessRoles'
        };
